SELECT
  s.subject_id,
  s.parent_id,
  s.termbase_id,
  lower(t.subject_name) AS code
FROM
  termeki_termbase_subjects s
  LEFT JOIN termeki_termbase_subject_translations t ON t.subject_id = s.subject_id
WHERE
  s.termbase_id IN
(5067233,8068645,8917007,3651782,8085495,2765619,9546447,1283851,9046585,6312621,4441577,6686780,5513125,7351963,8382224,
6234149,8547472,1794744,1871364,6811586,5215605,8243774,1186270,4057848,1631138,4168230,1550708,4278241,6294699,1718003,
4762067,8605248,1794179,1143464,4773332,5812536,9012994,2633923)
  AND t.lang = :lang
